import {
  Button,
  Modal,
  Table,
  TableContainer,
  Tbody,
  Td,
  Th,
  Thead,
  Tr,
  useDisclosure,
} from "@chakra-ui/react";
import { useState } from "react";

import { useLearningContent } from "../../_hooks/useLearningContent";

export const LearningContentTemplate = () => {
  // 内容リスト
  const { learningContents } = useLearningContent();

  // モーダルのモード
  const [isNew, setIsNew] = useState(false);
  // モーダルの状態
  const { isOpen, onOpen, onClose } = useDisclosure();

  const onClick = (id: string) => {
    console.debug(id);
  };

  return (
    <>
      <Button>新規</Button>
      <TableContainer>
        <Table variant="simple">
          <Thead bgColor="black">
            <Tr>
              <Th color="white">表示順</Th>
              <Th color="white">内容</Th>
              <Th color="white"></Th>
              <Th></Th>
            </Tr>
          </Thead>
          <Tbody>
            {learningContents.map((learningContent) => (
              <Tr>
                <Td>{learningContent.seq}</Td>
                <Td>{learningContent.contentName}</Td>
                <Td>
                  <Button onClick={() => onClick(learningContent.id)}>
                    編集
                  </Button>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      </TableContainer>
      <Modal
        isOpen={isOpen}
        selectedRecord={selectedRecord}
        onClose={onClose}
      ></Modal>
    </>
  );
};
